import { Component, Input, ChangeDetectionStrategy } from '@angular/core';

@Component({
  selector: 'ag-header-nav',
  template: `<nav>
    <div [ngClass]="classes">
      <ng-content></ng-content>
    </div>
  </nav>`,
  styleUrls: ['./headernav.css'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})

export class HeaderNavComponent {
  @Input() css?: string;
  public get classes(): string {
    let klasses = ['header-nav', this.css ? `${this.css}` : ''];
    klasses = klasses.filter((klass) => klass.length);
    return klasses.join(' ')
  }
}

@Component({
  selector: 'ag-header-nav-item',
  template: `<div [ngClass]="classes"><ng-content></ng-content></div>`,
  styleUrls: ['./headernavitem.css'],
})

export class HeaderNavItemComponent {
  @Input() css?: string;
  public get classes(): string {
    let klasses = ['header-nav-item', this.css ? `${this.css}` : ''];
    klasses = klasses.filter((klass) => klass.length);
    return klasses.join(' ');
  }
}

@Component({
  selector: 'ag-header',
  template: `<header [ngClass]="classes">
    <div class="header-content"><ng-content></ng-content></div>
  </header>
  `,
  styleUrls: ['./header.css'],
})

export class HeaderComponent {
  @Input() css?: string;
  @Input() isSkinned?: boolean = true;
  @Input() isSticky?: boolean;

  public get classes(): string {
    const baseClass = this.isSkinned ? 'header' : 'header-base';
    const isStickyClass = this.isSticky ? 'header-sticky' : '';
    const overrides = this.css ? `${this.css}` : '';
    let klasses = [
      baseClass,
      isStickyClass,
      overrides,
    ];
    klasses = klasses.filter((klass) => klass.length);
    return klasses.join(' ');
  }
}

styleUrlsData.data:
.header,
.header-base {
  display: flex;
  flex-direction: row;
  align-items: center;
}

.header-base img,
.header img {
  max-width: 100%;
  height: auto;
}

.header,
.header-skin {
  background-color: var(--agnosticui-header-background-color, var(--agnosticui-light));
  box-shadow: var(--agnosticui-header-box-shadow-hor, 0)
    var(--agnosticui-header-box-shadow-ver, 1px) var(--agnosticui-header-box-shadow-blur, 5px)
    var(--agnosticui-header-box-shadow-spread, 2px)
    var(--agnosticui-header-box-shadow-color, rgba(0, 0, 0, 0.1));
  height: var(--agnosticui-header-mobile-height, 128px);
  font-family: var(--agnosticui-header-font-family, var(--agnosticui-font-family));
  border-bottom: 1px solid var(--agnosticui-header-border-color, var(--agnosticui-gray-light));
}

.header-content {
  width: var(--agnosticui-header-content-width, 960px);
  max-width: 100%;
  margin: 0 auto;
}

.header .header-content {
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items: center;
  flex-wrap: wrap;
}

/**
 * If you make your header sticky, you should likely consider that jump links
 * e.g. <h1 id="Jump-Here">... will tuck underneath the header! Here's a recommendation
 * for how to circumnavigate this issue via scroll-margin-top:
  h1[id],
  h2[id],
  h3[id],
  h4[id],
  h5[id],
  h6[id] {
    scroll-margin-top: 80px;
  }
 * In this case I have a 64px tall header so I've added 16px for clearance -- you'll need
 * to curate your own use case, but scroll-margin-top is super useful for this use case.
 */

.header-sticky {
  position: relative;
  top: 0;
  z-index: 10;
}

@media (min-width: 960px) {
  .header-sticky {
    position: sticky;
  }

  .header .header-content {
    flex-direction: row;
    justify-content: space-between;
  }
  .header {
    height: var(--agnosticui-header-height, var(--fluid-64));
  }
}

