import { Component, Input, ChangeDetectionStrategy } from '@angular/core';

@Component({
  selector: 'ag-input',
  template: `<div className="width-full">
    <label [class]="labelClasses()" [for]="uniqueId">
      {{label}}
    </label>
    <ng-container *ngIf="type === 'textarea'; else noTextarea">
      <textarea *ngIf="type === 'textarea'"
        [id]="uniqueId"
        [name]="uniqueId"
        [value]="defaultValue || ''"
        [disabled]="isDisabled"
        [class]="inputClasses()"
        [placeholder]="placeholder ? placeholder : ''"
        [attr.rows]="rows"
        [attr.cols]="cols"
        (change)="handleChange"
        (focus)="handleFocus"
        (blur)="handleBlur"
      ></textarea>
    </ng-container>
    <ng-template #noTextarea>
      <div class="input-addon-container" *ngIf="hasLeftAddon || hasRightAddon else inputOnly">
        <ng-content select="[addOnLeft]"></ng-content>
        <input
          [id]="uniqueId"
          [name]="uniqueId"
          [value]="defaultValue || ''"
          [type]="type"
          [disabled]="isDisabled"
          [class]="inputClasses()"
          [placeholder]="placeholder ? placeholder : ''"
          (change)="handleChange"
          (focus)="handleFocus"
          (blur)="handleBlur"
        />
        <ng-content select="[addOnRight]"></ng-content>
      </div>
      <ng-template #inputOnly>
        <input
          [id]="uniqueId"
          [name]="uniqueId"
          [value]="defaultValue || ''"
          [type]="type"
          [disabled]="isDisabled"
          [class]="inputClasses()"
          [placeholder]="placeholder ? placeholder : ''"
          (change)="handleChange"
          (focus)="handleFocus"
          (blur)="handleBlur"
        />
      </ng-template>
    </ng-template>
    <span *ngIf="isInvalid; else helpHint" [class]="invalidClasses()" role="status" aria-live="polite">
      {{ invalidText }}
    </span>
    <ng-template #helpHint>
      <span [class]="helpClasses()" role="status" aria-live="polite">
      {{ helpText }}
      </span>
    </ng-template>
  </div>`,
  styleUrls: ['./input.css', './inputaddon-hack.css'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})

export class InputComponent {
  @Input() helpText?: string = '';
  @Input() invalidText?: string = '';
  @Input() isInvalid?: boolean = false;
  @Input() isSkinned?: boolean = true;
  @Input() isRounded?: boolean = false;
  @Input() isUnderlined?: boolean = false;
  @Input() isInline?: boolean = false;
  @Input() isDisabled?: boolean = false;
  @Input() hasLeftAddon?: boolean = false;
  @Input() hasRightAddon?: boolean = false;
  @Input() isUnderlinedWithBackground?: boolean = false;
  @Input() uniqueId!: string;
  @Input() label!: string;
  @Input() labelCss?: string;
  @Input() inputCss?: string;
  @Input() placeholder?: string;
  @Input() defaultValue?: string;
  @Input() size?: 'small' | 'large' | '' = '';
  @Input() type!: 'text' | 'password' | 'email' | 'number' | 'url' | 'tel' | 'search' | 'textarea';
  // It appears we can't have arbitrary props in Angular and they all have to be defined
  // as an @Input. React has ...rest, Vue has v-bind="$attrs", Svelte has  {...$$restProps} \_()_/
  // https://github.com/angular/angular/issues/14545
  @Input() rows?: number;
  @Input() cols?: number;

  helpClasses() {
    let klasses = [
      !this.size ? 'field-help' : '',
      this.size ? `field-help-${this.size}` : '',
    ]
    return klasses
      .filter((klass) => klass.length)
      .join(' ');
  }
  invalidClasses() {
    let klasses = [
      !this.size ? 'field-error' : '',
      this.size ? `field-error-${this.size}` : '',
    ]
    return klasses
      .filter((klass) => klass.length)
      .join(' ');
  }
  labelClasses() {
    let labelKlasses = [
      'label',
      this.isInvalid ? 'label-error' : '',
      this.isInline ? 'label-inline' : '',
      this.size ? `label-${this.size}`: '',
      this.labelCss ? this.labelCss : ''
    ];
    return labelKlasses
      .filter((klass) => klass.length)
      .join(' ');
  }
  inputClasses() {
    let klasses = [
      this.isSkinned ? 'input' : 'input-base',
      this.isRounded ? 'input-rounded' : '',
      this.isUnderlined ? 'input-underlined' : '',
      this.isDisabled ? 'disabled' : '',
      this.isInline ? 'input-inline' : '',
      this.hasLeftAddon ? 'input-has-left-addon': '',
      this.hasRightAddon ? 'input-has-right-addon' : '',
      this.isInvalid ? 'input-error' : '',
      this.isUnderlinedWithBackground ? 'input-underlined-bg' : '',
      this.inputCss ? `${this.inputCss}` : '',
      this.size ? `input-${this.size}` : '',
    ];
    klasses = klasses.filter((klass) => klass.length);
    return klasses.join(' ');
  }
  handleChange(ev: Event) {
    console.log('handleChange not yet implemented...');
  }
  handleFocus(ev: Event) {
    console.log('handleFocus not yet implemented...');
  }
  handleBlur(ev: Event) {
    console.log('handleBlur not yet implemented...');
  }
}

styleUrlsData.data:
.input-base,
.input {
  /* Note this cannot be user-select: none else mobile safari won't accept input:
  https://stackoverflow.com/questions/49889003/cannot-write-into-input-field-on-safari/49901069
   */
  user-select: initial;
  appearance: none;
  box-sizing: border-box;
  /* Use the same color for the cursor */
  caret-color: currentColor;
}

.label,
.label-base {
  padding: 0;
  border: 0;
  box-sizing: border-box;
  font-family: inherit;
}

/* Electing to scope these as opposed to doing :root level definitions */
.field-help,
.field-help-large,
.field-help-small,
.field-error,
.field-error-large,
.field-error-small,
.label-skin,
.label,
.input-addon-container,
.input-small,
.input-large,
.input-skin,
.input-underlined,
.input-underlined-bg,
.input {
  color: var(--agnosticui-font-color, var(--agnosticui-dark));
  font-family: var(
    --agnosticui-font-family,
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    'Open Sans',
    'Ubuntu',
    'Fira Sans',
    Helvetica,
    'Droid Sans',
    'Helvetica Neue',
    sans-serif,
    'Apple Color Emoji',
    'Segoe UI Emoji',
    'Segoe UI Symbol'
  );
  font-weight: var(--agnosticui-font-weight, 300);
  font-size: var(--agnosticui-font-size, 1rem);
  line-height: var(--agnosticui-line-height, 1.25rem);
  width: 100%;
  max-width: 100%;
}

.input-skin,
.input {
  /* seems like a reasonable default as chrome picks `outset` which results in a weird 3d effect */
  border-style: solid;
  /* this can be overriden, but it might mess with the balance of the button heights across variants */
  border-width: var(--agnosticui-input-border-size, 1px);
  border-color: var(--agnosticui-input-border-color, var(--agnosticui-gray-light));
  /* these can be overriden, but it might mess with the balance of the inputheights across variants */
  padding-block-start: var(--agnosticui-input-vertical-pad, 0.5rem);
  padding-block-end: var(--agnosticui-input-vertical-pad, 0.5rem);
  padding-inline-start: var(--agnosticui-input-side-padding, 0.75rem);
  padding-inline-end: var(--agnosticui-input-side-padding, 0.75rem);

  /* Note we only want to set properties that we actually want
  to transition in. For example, if we transition "all", the
  inputs will "grow in" on page loadnot a happy effect :) */
  transition-property: box-shadow;
  transition-duration: var(--agnosticui-input-timing, var(--agnosticui-timing-medium));
}

.label {
  display: inline-block;
  /* Provided --agnosticui-input-vertical-pad isn't overriden we'll get 20px
  label w/a 6px margin then a 38px input = 64 which is on the 8pt grid */
  margin-block-start: 0;
  margin-inline-start: 0;
  margin-inline-end: 0;
  margin-block-end: var(--agnosticui-input-label-pad, 0.375rem);
  vertical-align: initial;
}

/* Reset labels and field errors to be 2px less then input font size */
.field-help,
.field-error,
.label,
.label-skin {
  font-size: calc(var(--agnosticui-font-size, 1rem) - 2px);
}

.label-inline,
.input-inline {
  width: auto;
}

/* When inlined, the margin-block-end will throw the label off-center with adjacent input */
.label-inline {
  margin-block-start: 0;
  margin-block-end: 0;
  margin-inline-start: 0;
  margin-inline-end: var(--agnosticui-input-side-padding, 0.75rem);
}

/**
* Placeholder
*/
.input::-webkit-input-placeholder {
  color: currentColor;
  opacity: 0.5;
  transition: opacity var(--agnosticui-timing-fast) ease-out;
}

.input::-moz-placeholder {
  color: currentColor;
  opacity: 0.5;
  transition: opacity var(--agnosticui-timing-fast) ease-out;
}

.input::-ms-placeholder {
  color: currentColor;
  opacity: 0.5;
  transition: opacity var(--agnosticui-timing-fast) ease-out;
}

.input:-ms-placeholder {
  color: currentColor;
  opacity: 0.5;
  transition: opacity var(--agnosticui-timing-fast) ease-out;
}

/**
* Underlined inputs
*/
.input-underlined {
  border-top: 0;
  border-left: 0;
  border-right: 0;
  border-color: var(--agnosticui-input-underlined-color, var(--agnosticui-gray-mid-dark));
  background-color: transparent;
}

.input-underlined-bg {
  background-color: var(--agnosticui-input-underlined-bg-color, var(--agnosticui-gray-extra-light));
}

/**
* Rounded inputs
*/
.input-rounded {
  border-radius: var(--agnosticui-border-radius, 0.25rem);
}

/**
 * Errors
 * We provide a class-based approach to setting errors which means we do
 * not support :invalid, so it requires custom use of html4 validation API
 * (see https://developer.mozilla.org/en-US/docs/Learn/Forms/Form_validation
 * of the example in corresponding input.html file). The reason I elected to
 * not include :invalid, is it seems to result in "shouting" at the user as
 * I did not find an easy way to only kick in errors after a certain number
 * of characters have been type (blur is actually better but I did not
 * implement that in the contrived example).
 */
.label-error {
  color: var(--agnosticui-input-error-color, var(--agnosticui-error-color));
}
.input-error {
  border-color: var(--agnosticui-input-error-color, var(--agnosticui-error-color));
}

.label-error,
.field-error,
.field-error-small,
.field-error-large {
  color: var(--agnosticui-input-error-color, var(--agnosticui-error-color));
}

.field-help,
.field-help-small,
.field-help-large {
  color: var(--agnosticui-input-help-color, var(--agnosticui-gray-dark));
}

.field-help,
.field-help-small,
.field-help-large,
.field-error,
.field-error-small,
.field-error-large {
  display: inline-block;
  width: 100%;
  margin-block-start: calc(var(--agnosticui-input-vertical-pad, 0.5rem) / 2);
}

/**
  * Sizes
  */
.input-large {
  font-size: calc(var(--agnosticui-font-size, 1rem) + 0.25rem);
  line-height: 1.6rem;
}

.field-help-large,
.field-error-large,
.label-large {
  /* We initially remove -2px from font-size so setting to font-size essentially adds the 2px back */
  font-size: var(--agnosticui-font-size, 1rem);
}

.input-small {
  font-size: calc(var(--agnosticui-font-size, 1rem) - 0.25rem);
  line-height: 1rem;
}

.field-help-small,
.field-error-small,
.label-small {
  font-size: calc(var(--agnosticui-font-size, 1rem) - 0.25rem);
}

.input:focus {
  box-shadow: 0 0 0 3px var(--agnosticui-focus-ring-color);
  /* High contrast mode outline */
  outline: 3px solid transparent;
  transition: box-shadow var(--agnosticui-timing-fast) ease-out;
}
/* Set the focus to transparent when there's an error since we use
borders that visually conflict. */
.input-error:focus {
  box-shadow: 0 0 0 3px transparent;
}

/*
* Disabled State
*
* The disabled state uses the class .disabled,
* and the form attribute disabled="disabled".
* The use of !important is only added because this is a state
* that must be applied to all inputs when in a disabled state.
*/
.input.disabled, /* DEPRECATED -- TODO remove class based disabled */
.input:disabled {
  background: var(--agnosticui-input-disabled-bg, var(--agnosticui-disabled-bg)) !important;
  color: var(--agnosticui-input-disabled-color, var(--agnosticui-disabled-color)) !important;
  appearance: none !important;
  box-shadow: none !important;
  cursor: not-allowed !important;
  opacity: 0.8 !important;
}

@media screen and (-ms-high-contrast: active) {
  /* High contrast mode outline hacks */
  .input:disabled {
    outline: 2px solid transparent;
    outline-offset: -2px;
  }
}

/**
 * Input "has addon"
 */

.input-addon-container {
  display: flex;
  position: relative;
  width: 100%;
  /* Maybe I should have defined another css prop for addon adjustments but trying
  to avoid any extra variable explosion there--will need to keep an eye on this */
  --addon-padding: calc(var(--agnosticui-input-side-padding, 0.75rem) * 1.5);
}

.input-has-left-addon,
.input-has-right-addon {
  flex: 1;
}

.input-has-left-addon {
  padding-inline-start: calc(var(--addon-padding) * 2.25);
}

.input-has-right-addon {
  padding-inline-end: calc(var(--addon-padding) * 2.25);
}

/*
* Input addons e.g. icons inside the input
*/
.input-addon-right,
.input-addon-left {
  /* Maybe I should have defined another css prop for addon adjustments but trying
  to avoid any extra variable explosion there--will need to keep an eye on this */
  --addon-padding: calc(var(--agnosticui-input-side-padding) * 1.5);
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
}

.input-addon-left {
  left: var(--addon-padding);
}

.input-addon-right {
  right: var(--addon-padding);
}

/* Angular projected content is hard to add styles too especially if
using ng-content select[foo] which we are :( */
:host ::ng-deep .addOnLeft,
:host ::ng-deep .addOnRight {
  --addon-padding: calc(var(--agnosticui-input-side-padding) * 1.5);
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
}

:host ::ng-deep .addOnLeft {
  left: var(--addon-padding);
}

:host ::ng-deep .addOnRight {
  right: var(--addon-padding);
}

